<script>
export default {
  methods: {
    onWaypoint({ going }) {
      if (going === this.$waypointMap.GOING_IN) {
        this.$el.classList.add('go')
      }
      if (going === this.$waypointMap.GOING_OUT) {
        this.$el.classList.remove('go')
      }
    },
  },
}
</script>

